INSERT INTO `news` (`news_id`, `news_title`, `news_text`, `news_date`, `news_order`) VALUES
(1, 'Летний лагерь по Кекусинкай карате', '<p>С 8 до 12 мая в г. Баку, рес. Азербайджан был проведен летний лагерь по Кекусинкай карате.</p>\r\n\r\n<p>В семинаре участвовали 521 спортсмен из разных стран. Международный семинар в летнем лагере возглавлял Президент Международной Организации Тейюб Азизов (9 дан). Во время семинара были отработаны все техники Кекусинкай карате, так же было показано и объяснено техника нового боевого стиля &laquo;S&raquo; версии. Сам лично канчо создатель IKKO &laquo;S&raquo; версии показал приемы боя.<br />\r\n<br />\r\nСеминар проводился на протяжении 5 дней. В последний день семинара был проведен дан-тест, который принимал сам канчо. Спортсмены успешно прошли дан-тест и сдали экзамен.</p>', 1340721465, 0),
(2, 'Кубок мира - 2012', '<p style="text-align:center"><strong>30.11.2012 &ndash; 03.12.2012<br />\r\nТурция, Истанбул<br />\r\n15 стран, 289 участников</strong></p>\r\n\r\n<p><strong>Девушки 50 кг</strong><br />\r\nI Hayrunisa Duman (Tur)<br />\r\nII Turgci Yagci (Tur)<br />\r\nIII Nazarova Manizha (Taj)<br />\r\nIII Grigoriyants Zhanna (Ukr)<br />\r\n<br />\r\n<strong>Девушки 55 кг</strong><br />\r\nI Abdulsalamova Saltanat (Rus)<br />\r\nII Hatice Ozderya (Tur)<br />\r\nIII Gamze Fazlioglu (Tur)<br />\r\nIII Pajkovska Ema (Pol)<br />\r\n<br />\r\n<strong>Девушки 60 кг</strong><br />\r\nI Gulsum Fazlioglu (Tur)<br />\r\nII Tuba Kilic (Tur)<br />\r\nIII Jazinski Anna (Bel)<br />\r\nIII Bolqova Natali (KGZ)<br />\r\n<br />\r\n<strong>Женщины 60 кг</strong><br />\r\n<br />\r\nI Berna Aydin (Tur)<br />\r\nII Melina Ozdemir (Tur)<br />\r\nIII Nikolayeva Liliya (Ukr)<br />\r\nIII Fedorichtcheva Tatyana (Bel)<br />\r\n<br />\r\n<strong>Женщины 60+ кг</strong><br />\r\nI Turce Topuz (Tur)<br />\r\nII Buse Tpuz (Tur)<br />\r\nIII Yucenko Tamara (Uzb)<br />\r\nIII Herry Agneiszka (Pol)<br />\r\n<br />\r\n<br />\r\n<strong>Мальчики 50 кг</strong><br />\r\nI Dzhalilov Sirozhiddin (Taj)<br />\r\nII Shahbazli Elgiz (Aze)<br />\r\nIII Omer Ceylan (Tur)<br />\r\nIII Nurrulloyev Farzov (Taj)<br />\r\n<br />\r\n<strong>Мальчики 55 кг</strong><br />\r\nI Berkay Shahin (Tur)<br />\r\nII Szjovski Artur (Pol)<br />\r\nIII Startsev Dmitriy (Ukr)<br />\r\nIII Shouvalov Ilya (Bel)<br />\r\n<br />\r\n<strong>Мальчики 60 кг</strong><br />\r\n<br />\r\nI Ibragimov Israpil (Rus)<br />\r\nII Koromatov Parviz (Taj)<br />\r\nIII Dovlatov Untamzhon (Taj)<br />\r\nIII Ismailov Rashad (Aze)<br />\r\n<br />\r\n<strong>Мальчики 65 кг</strong><br />\r\nI Akhkubegov Akhubeg (Rus)<br />\r\nII Erkan Ozbey (Tur)<br />\r\nIII Muhammet Topcu (Tur)<br />\r\nIII Barish Akdag (Tur)<br />\r\n<br />\r\n<strong>Мальчики 70 кг</strong><br />\r\nI Izettin Uqurlu (Tur)<br />\r\nII Evrimsam Karagoz (Tur)<br />\r\nIII